	public static void addCookie(HttpServletResponse resp, String cookieName, String cookieValue) {
		Cookie ck = new Cookie(cookieName, cookieValue);
		resp.addCookie(ck);
	}

	public static void SignOut(HttpServletResponse resp, Cookie ck[]) {
		if (ck == null) {
			return;
		}
		for (Cookie ck1 : ck) {
			if (ck1.getName().equals("username")) {
				ck1.setMaxAge(0);
				resp.addCookie(ck1);
				return;
			}
		}
	}

	public static void checkSignInStatus(HttpServletRequest request, HttpServletResponse response)
			throws ServletException, IOException {
		PrintWriter pw = response.getWriter();
		Cookie ck[] = request.getCookies();
		if (Resource.getUsername(ck).equals("")) {
			Resource.redirect(response, Resource.url_SignIn);
			return;
		} else {
			Resource.createSignOutButton(pw);
		}
	}
